import React, { useEffect, useState } from 'react';
import axios from 'axios';
import EmployeeProfile from './EmployeeProfile';
import { Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Avatar, Typography, TextField, Button, Box, Tooltip, InputAdornment, Divider } from '@mui/material';
import MaleIcon from '@mui/icons-material/Male';
import FemaleIcon from '@mui/icons-material/Female';
import InfoIcon from '@mui/icons-material/Info';
import PersonIcon from '@mui/icons-material/Person';
import SearchIcon from '@mui/icons-material/Search';


function EmployeeList({ onBack }) {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');
  const [profileOpen, setProfileOpen] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/employees')
      .then(res => {
        setEmployees(res.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to fetch employees');
        setLoading(false);
      });
  }, []);

  const showProfile = (emp) => {
    setSelectedEmployee(emp);
    setProfileOpen(true);
  };

  const handleProfileClose = () => {
    setProfileOpen(false);
    setSelectedEmployee(null);
  };

  const handleProfileUpdate = (updatedEmp) => {
    setEmployees(prev => prev.map(emp => emp._id === updatedEmp._id ? updatedEmp : emp));
    setProfileOpen(false);
    setSelectedEmployee(null);
  };

  // Filter employees by NIC number only
  const filtered = employees.filter(emp =>
    emp.nicNumber && emp.nicNumber.toLowerCase().includes(search.toLowerCase())
  );

  if (loading) return <Typography align="center" sx={{ mt: 4 }}>Loading employees...</Typography>;
  if (error) return <Typography color="error" align="center" sx={{ mt: 4 }}>{error}</Typography>;

  return (
    <Box
      sx={{
        minHeight: '100vh',
        width: '100vw',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #e3f2fd 60%, #fff 100%)',
        overflow: 'hidden',
        py: 4,
        pl: 0,
      }}
    >
      <Box sx={{ maxWidth: 900, width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'flex-start', justifyContent: 'center', mx: 0 }}>
        <Typography
          variant="h3"
          align="left"
          sx={{
            color: '#1976d2',
            fontWeight: 900,
            letterSpacing: 2,
            mb: 2,
            textShadow: '0 2px 8px #b3c6e0',
            width: '100%',
            textAlign: 'left',
          }}
        >
          EMPLOYEE SYSTEM
        </Typography>
        <Paper
          elevation={2}
          sx={{
            maxWidth: 900,
            width: '100%',
            margin: 0,
            my: 2,
            p: 2,
            borderRadius: 4,
            background: '#fff',
            boxShadow: 2,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'flex-start',
          }}
        >
        <Box display="flex" flexDirection="column" alignItems="center" mb={2}>
          <Avatar sx={{ bgcolor: '#1976d2', width: 64, height: 64, mb: 1 }}>
            <PersonIcon sx={{ fontSize: 40 }} />
          </Avatar>
          <Typography variant="h5" sx={{ fontWeight: 700, color: '#1976d2', letterSpacing: 1 }}>Employee List</Typography>
        </Box>
        <Divider sx={{ mb: 2 }} />
        <Box display="flex" justifyContent="center" alignItems="center" mb={3} width="100%">
          <TextField
            variant="outlined"
            placeholder="Search by NIC number"
            value={search}
            onChange={e => setSearch(e.target.value)}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon color="primary" sx={{ mr: 1 }} />
                </InputAdornment>
              )
            }}
            sx={{ width: { xs: '100%', sm: 340 }, borderRadius: 3, background: '#fff' }}
          />
        </Box>
        {filtered.length === 0 ? (
          <Typography align="center" color="text.secondary">No employees found.</Typography>
        ) : (
          <Box display="flex" justifyContent="center" alignItems="center" width="100%" mt={2}>
            <Paper
              elevation={4}
              sx={{
                width: '100%',
                maxWidth: 820,
                p: 2,
                borderRadius: 3,
                boxShadow: 6,
                border: '1.5px solid #1976d2',
                background: '#f8fafc',
                mx: 'auto',
              }}
            >
              <TableContainer sx={{ borderRadius: 2, boxShadow: 0, background: 'transparent' }}>
                <Table
                  sx={{
                    width: '100%',
                    tableLayout: 'fixed',
                    minWidth: 700,
                  }}
                  size="medium"
                >
                  <TableHead sx={{ background: 'linear-gradient(90deg, #1976d2 60%, #42a5f5 100%)' }}>
                    <TableRow>
                      <TableCell sx={{ color: '#1976d2', fontWeight: 900, fontSize: 26, py: 2.5, px: 2.5, background: '#f5f5f5' }}>Photo</TableCell>
                      <TableCell sx={{ color: '#1976d2', fontWeight: 900, fontSize: 26, py: 2.5, px: 2.5, background: '#f5f5f5' }}>Full Name</TableCell>
                      <TableCell sx={{ color: '#1976d2', fontWeight: 900, fontSize: 26, py: 2.5, px: 2.5, background: '#f5f5f5' }}>NIC Number</TableCell>
                      <TableCell sx={{ color: '#1976d2', fontWeight: 900, fontSize: 26, py: 2.5, px: 2.5, background: '#f5f5f5' }}>Date of Birth</TableCell>
                      <TableCell sx={{ color: '#1976d2', fontWeight: 900, fontSize: 26, py: 2.5, px: 2.5, background: '#f5f5f5' }}>Age</TableCell>
                      <TableCell sx={{ color: '#1976d2', fontWeight: 900, fontSize: 26, py: 2.5, px: 2.5, background: '#f5f5f5' }}>Sex</TableCell>
                      <TableCell sx={{ color: '#1976d2', fontWeight: 900, fontSize: 26, py: 2.5, px: 2.5, background: '#f5f5f5' }}>District</TableCell>
                      <TableCell sx={{ color: '#1976d2', fontWeight: 900, fontSize: 26, py: 2.5, px: 2.5, background: '#f5f5f5' }}>Permanent Address</TableCell>
                      <TableCell sx={{ color: '#1976d2', fontWeight: 900, fontSize: 26, py: 2.5, px: 2.5, background: '#f5f5f5' }}>Temporary Address</TableCell>
                      <TableCell sx={{ color: '#1976d2', fontWeight: 900, fontSize: 26, py: 2.5, px: 2.5, background: '#f5f5f5' }}>Contact Details</TableCell>
                      <TableCell sx={{ color: '#1976d2', fontWeight: 900, fontSize: 26, py: 2.5, px: 2.5, background: '#f5f5f5' }}>Profile</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {filtered.map((emp, idx) => {
                      return (
                        <TableRow
                          key={emp._id}
                          hover
                          sx={theme => ({
                            background: idx % 2 === 0 ? '#f9f9fb' : '#e3f2fd',
                            fontSize: 24,
                            height: 80,
                            transition: 'background 0.2s',
                            '&:hover': {
                              background: '#bbdefb',
                            },
                            borderBottom: '2px solid #e0e0e0',
                          })}
                        >
                          <TableCell sx={{ px: 2.5, py: 2 }}>
                            {emp.photo ? (
                              <Avatar src={`data:image/*;base64,${emp.photo}`} alt={emp.fullName} sx={{ width: 40, height: 40, mx: 'auto', border: '1px solid #1976d2' }} />
                            ) : (
                              <Avatar sx={{ width: 40, height: 40, mx: 'auto', bgcolor: '#bdbdbd' }}>
                                <PersonIcon sx={{ fontSize: 20 }} />
                              </Avatar>
                            )}
                          </TableCell>
                          <TableCell sx={{ fontSize: 24, px: 2.5, py: 2 }}>
                            <Tooltip title={emp.fullName} arrow>
                              <span>{emp.fullName}</span>
                            </Tooltip>
                          </TableCell>
                          <TableCell sx={{ fontSize: 24, px: 2.5, py: 2 }}>
                            <Tooltip title={emp.nicNumber} arrow>
                              <span>{emp.nicNumber}</span>
                            </Tooltip>
                          </TableCell>
                          <TableCell sx={{ fontSize: 24, px: 2.5, py: 2 }}>
                            <Tooltip title={new Date(emp.dateOfBirth).toLocaleDateString()} arrow>
                              <span>{new Date(emp.dateOfBirth).toLocaleDateString()}</span>
                            </Tooltip>
                          </TableCell>
                          <TableCell sx={{ fontSize: 24, px: 2.5, py: 2 }}>
                            <Tooltip title={emp.age ? emp.age.toString() : ''} arrow>
                              <span>{emp.age}</span>
                            </Tooltip>
                          </TableCell>
                          <TableCell sx={{ fontSize: 24, px: 2.5, py: 2 }}>
                            {emp.sex === 'Male' ? (
                              <Tooltip title="Male"><MaleIcon sx={{ color: '#1976d2', fontSize: 32 }} /></Tooltip>
                            ) : emp.sex === 'Female' ? (
                              <Tooltip title="Female"><FemaleIcon sx={{ color: '#e91e63', fontSize: 32 }} /></Tooltip>
                            ) : (
                              <Tooltip title={emp.sex}><InfoIcon sx={{ color: '#757575', fontSize: 32 }} /></Tooltip>
                            )}
                          </TableCell>
                          <TableCell sx={{ fontSize: 24, px: 2.5, py: 2 }}>
                            <Tooltip title={emp.district} arrow>
                              <span>{emp.district}</span>
                            </Tooltip>
                          </TableCell>
                          <TableCell sx={{ fontSize: 24, px: 2.5, py: 2 }}>
                            <Tooltip title={emp.permanentAddress} arrow>
                              <span>{emp.permanentAddress}</span>
                            </Tooltip>
                          </TableCell>
                          <TableCell sx={{ fontSize: 24, px: 2.5, py: 2 }}>
                            <Tooltip title={emp.temporaryAddress} arrow>
                              <span>{emp.temporaryAddress}</span>
                            </Tooltip>
                          </TableCell>
                          <TableCell sx={{ fontSize: 24, px: 2.5, py: 2 }}>
                            <Tooltip title={emp.contactDetails} arrow>
                              <span>{emp.contactDetails}</span>
                            </Tooltip>
                          </TableCell>
                          <TableCell sx={{ px: 1, py: 0.75 }}>
                            {!profileOpen && (
                              <Tooltip title="Show Profile" arrow>
                                <Button
                                  variant="contained"
                                  color="primary"
                                  size="medium"
                                  sx={{ borderRadius: 2, fontWeight: 600, px: 2, letterSpacing: 1, fontSize: 18, minWidth: 40 }}
                                  onClick={() => showProfile(emp)}
                                >
                                  <InfoIcon sx={{ fontSize: 24, color: '#fff' }} />
                                </Button>
                              </Tooltip>
                            )}
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
              </TableContainer>
            </Paper>
          </Box>
        )}
        <EmployeeProfile
          open={profileOpen}
          onClose={handleProfileClose}
          employee={selectedEmployee}
          onUpdate={handleProfileUpdate}
        />
        {onBack && (
          <Button
            onClick={onBack}
            variant="contained"
            color="secondary"
            size="large"
            sx={{
              marginTop: 4,
              borderRadius: 3,
              px: 4,
              py: 1.5,
              fontWeight: 600,
              fontSize: 18,
              letterSpacing: 1,
              boxShadow: '0 2px 8px #b3c6e0',
              display: 'block',
              mx: 'auto',
            }}
          >
             Home
          </Button>
        )}
      </Paper>
      </Box>
    </Box>
  );
}

export default EmployeeList;
