import React, { useEffect, useState } from 'react';
import axios from 'axios';
import EmployeeProfile from './EmployeeProfile';

function EmployeeList() {
  const [profileOpen, setProfileOpen] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const showProfile = (emp) => {
    setSelectedEmployee(emp);
    setProfileOpen(true);
  };
  const handleProfileClose = () => {
    setProfileOpen(false);
    setSelectedEmployee(null);
  };
  const handleProfileUpdate = () => {
    setProfileOpen(false);
    setSelectedEmployee(null);
    // Refresh employee list after update
    setLoading(true);
    axios.get('http://localhost:5000/api/employees')
      .then(res => {
        setEmployees(res.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to fetch employees');
        setLoading(false);
      });
  };
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/employees')
      .then(res => {
        setEmployees(res.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to fetch employees');
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading employees...</p>;
  if (error) return <p>{error}</p>;

  // Filter employees by NIC number only
  const filtered = employees.filter(emp =>
    emp.nicNumber.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h2 style={{ textAlign: 'center', color: '#1976d2', marginBottom: 24 }}>Employee List</h2>
      <div style={{ display: 'flex', justifyContent: 'center', marginBottom: 16 }}>
        <input
          type="text"
          placeholder="Search by NIC number"
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{ padding: 8, width: 300, fontSize: 16, borderRadius: 4, border: '1px solid #b3c6e0' }}
        />
      </div>
      {filtered.length === 0 ? (
        <p>No employees found.</p>
      ) : (
        <table border="1" cellPadding="8">
          <thead>
            <tr>
              <th>Photo</th>
              <th>Full Name</th>
              <th>NIC Number</th>
              <th>Date of Birth</th>
              <th>Age</th>
              <th>Sex</th>
              <th>District</th>
              <th>Permanent Address</th>
              <th>Temporary Address</th>
              <th>Contact Details</th>
              <th>Profile</th>
            </tr>
          </thead>
          <tbody>
            {filtered.map(emp => (
              <tr key={emp._id}>
                <td>
                  {emp.photo ? (
                    <img src={`data:image/*;base64,${emp.photo}`} alt="Employee" style={{ width: 48, height: 48, objectFit: 'cover', borderRadius: '50%', border: '1px solid #ccc' }} />
                  ) : (
                    <span style={{ color: '#bbb' }}>No Photo</span>
                  )}
                </td>
                <td>{emp.fullName}</td>
                <td>{emp.nicNumber}</td>
                <td>{new Date(emp.dateOfBirth).toLocaleDateString()}</td>
                <td>{emp.age}</td>
                <td>{emp.sex}</td>
                <td>{emp.district}</td>
                <td>{emp.permanentAddress}</td>
                <td>{emp.temporaryAddress}</td>
                <td>{emp.contactDetails}</td>
                <td>
                  {!profileOpen && (
                    <button
                      onClick={() => showProfile(emp)}
                      style={{ padding: '6px 16px', borderRadius: 6, border: 'none', background: '#1976d2', color: '#fff', cursor: 'pointer', fontWeight: 500 }}
                    >
                      Show Profile
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <EmployeeProfile
        open={profileOpen}
        onClose={handleProfileClose}
        employee={selectedEmployee}
        onUpdate={handleProfileUpdate}
      />
    </div>
  );
}

export default EmployeeList;
