import React, { useEffect, useState } from 'react';
import axios from 'axios';
import EmployeeProfile from './EmployeeProfile';
import { Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Avatar, Typography, TextField, Button, Box, Tooltip } from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import SearchIcon from '@mui/icons-material/Search';

function EmployeeList() {
  const [profileOpen, setProfileOpen] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);

  // Accept onBack prop for navigation
  const props = arguments[0] || {};
  const onBack = props.onBack;
  const showProfile = (emp) => {
    setSelectedEmployee(emp);
    setProfileOpen(true);
  };
  const handleProfileClose = () => {
    setProfileOpen(false);
    setSelectedEmployee(null);
  };
  const handleProfileUpdate = () => {
    setProfileOpen(false);
    setSelectedEmployee(null);
    // Refresh employee list after update
    setLoading(true);
    axios.get('http://localhost:5000/api/employees')
      .then(res => {
        setEmployees(res.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to fetch employees');
        setLoading(false);
      });
  };
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/employees')
      .then(res => {
        setEmployees(res.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to fetch employees');
        setLoading(false);
      });
  }, []);

  if (loading) return <Typography align="center" sx={{ mt: 4 }}>Loading employees...</Typography>;
  if (error) return <Typography color="error" align="center" sx={{ mt: 4 }}>{error}</Typography>;

  // Filter employees by NIC number only
  const filtered = employees.filter(emp =>
    emp.nicNumber.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Paper elevation={6} sx={{ maxWidth: 1200, mx: 'auto', my: 5, p: 3, borderRadius: 4, background: 'linear-gradient(135deg, #e3f2fd 60%, #fff 100%)', boxShadow: 8 }}>
      <Typography variant="h5" align="center" sx={{ color: '#1976d2', fontWeight: 700, mb: 3, letterSpacing: 1 }}>Employee List</Typography>
      <Box display="flex" justifyContent="center" alignItems="center" mb={3}>
        <TextField
          variant="outlined"
          placeholder="Search by NIC number"
          value={search}
          onChange={e => setSearch(e.target.value)}
          InputProps={{
            startAdornment: (
              <SearchIcon color="primary" sx={{ mr: 1 }} />
            ),
            sx: { borderRadius: 3, background: '#fff' }
          }}
          sx={{ width: 340 }}
        />
      </Box>
      {filtered.length === 0 ? (
        <Typography align="center" color="text.secondary">No employees found.</Typography>
      ) : (
        <TableContainer component={Paper} sx={{ borderRadius: 3, boxShadow: 3 }}>
          <Table>
            <TableHead sx={{ background: 'linear-gradient(90deg, #1976d2 60%, #42a5f5 100%)' }}>
              <TableRow>
                <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Photo</TableCell>
                <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Full Name</TableCell>
                <TableCell sx={{ color: '#fff', fontWeight: 700 }}>NIC Number</TableCell>
                <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Date of Birth</TableCell>
                <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Age</TableCell>
                <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Sex</TableCell>
                <TableCell sx={{ color: '#fff', fontWeight: 700 }}>District</TableCell>
                <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Permanent Address</TableCell>
                <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Temporary Address</TableCell>
                <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Contact Details</TableCell>
                <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Profile</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {filtered.map(emp => (
                <TableRow key={emp._id} hover sx={{ background: '#f8fafc' }}>
                  <TableCell>
                    {emp.photo ? (
                      <Avatar src={`data:image/*;base64,${emp.photo}`} alt={emp.fullName} sx={{ width: 48, height: 48, mx: 'auto', border: '2px solid #1976d2' }} />
                    ) : (
                      <Avatar sx={{ width: 48, height: 48, mx: 'auto', bgcolor: '#bdbdbd' }}>
                        <PersonIcon />
                      </Avatar>
                    )}
                  </TableCell>
                  <TableCell>{emp.fullName}</TableCell>
                  <TableCell>{emp.nicNumber}</TableCell>
                  <TableCell>{new Date(emp.dateOfBirth).toLocaleDateString()}</TableCell>
                  <TableCell>{emp.age}</TableCell>
                  <TableCell>{emp.sex}</TableCell>
                  <TableCell>{emp.district}</TableCell>
                  <TableCell>{emp.permanentAddress}</TableCell>
                  <TableCell>{emp.temporaryAddress}</TableCell>
                  <TableCell>{emp.contactDetails}</TableCell>
                  <TableCell>
                    {!profileOpen && (
                      <Tooltip title="Show Profile" arrow>
                        <Button
                          variant="contained"
                          color="primary"
                          size="small"
                          sx={{ borderRadius: 2, fontWeight: 600, px: 2, letterSpacing: 1 }}
                          onClick={() => showProfile(emp)}
                        >
                          Profile
                        </Button>
                      </Tooltip>
                    )}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
      <EmployeeProfile
        open={profileOpen}
        onClose={handleProfileClose}
        employee={selectedEmployee}
        onUpdate={handleProfileUpdate}
      />
      {onBack && (
        <Button
          onClick={onBack}
          variant="contained"
          color="secondary"
          size="large"
          sx={{
            marginTop: 4,
            borderRadius: 3,
            px: 4,
            py: 1.5,
            fontWeight: 600,
            fontSize: 18,
            letterSpacing: 1,
            boxShadow: '0 2px 8px #b3c6e0',
            display: 'block',
            mx: 'auto'
          }}
        >
          ‚Üê Home
        </Button>
      )}
    </Paper>
  );
}

export default EmployeeList;
