import React, { useState } from 'react';
import EmployeeForm from './EmployeeForm';
import EmployeeList from './EmployeeList';
import { Typography, Button } from '@mui/material';

function App() {
  const [showList, setShowList] = useState(false);
  const handleSuccess = () => setShowList(true);
  return (
    <div style={{ position: 'relative', minHeight: '100vh', background: '#f5f6fa' }}>
      {/* Button absolutely positioned in top right */}
      {!showList && (
        <Button
          variant="contained"
          color="primary"
          size="large"
          startIcon={<span role="img" aria-label="list" style={{ fontSize: 22 }}>ðŸ“‹</span>}
          sx={{
            position: 'absolute',
            top: 32,
            right: 32,
            borderRadius: 3,
            px: 3,
            py: 1.5,
            fontWeight: 600,
            fontSize: 18,
            letterSpacing: 1,
            boxShadow: '0 2px 8px #b3c6e0',
            background: 'linear-gradient(90deg, #1976d2 60%, #42a5f5 100%)',
            mb: 2
          }}
          onClick={() => setShowList(true)}
        >
          Show Employee List
        </Button>
      )}
      <Typography variant="h4" component="h1" sx={{ fontWeight: 600, color: '#1976d2', textAlign: 'center', mt: 5, mb: 3, textTransform: 'uppercase' }}>
        EMPLOYEE SYSTEM
      </Typography>
      <div style={{ maxWidth: 600, margin: '32px auto 0 auto' }}>
        {!showList ? (
          <EmployeeForm onSuccess={handleSuccess} />
        ) : (
          <EmployeeList onBack={() => setShowList(false)} />
        )}
      </div>
    </div>
  );
}

export default App;
