import React, { useState } from 'react';
import EmployeeForm from './EmployeeForm';
import EmployeeList from './EmployeeList';
import { Typography } from '@mui/material';

function App() {
  const [showList, setShowList] = useState(false);
  const handleSuccess = () => setShowList(true);
  return (
    <div style={{ position: 'relative', minHeight: '100vh', background: '#f5f6fa' }}>
      {/* Top bar with button on right */}
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '24px 32px 0 32px' }}>
        <Typography variant="h4" component="h1" sx={{ fontWeight: 600, color: '#1976d2', textAlign: 'center', flex: 1 }}>
          Employee System Frontend
        </Typography>
        {!showList && (
          <button
            onClick={() => setShowList(true)}
            style={{
              padding: '10px 28px',
              fontSize: 16,
              borderRadius: 24,
              border: 'none',
              background: 'linear-gradient(90deg, #1976d2 60%, #42a5f5 100%)',
              color: '#fff',
              cursor: 'pointer',
              fontWeight: 500,
              boxShadow: '0 2px 8px #b3c6e0',
              marginLeft: 32
            }}
          >
            Show Employee List
          </button>
        )}
      </div>
      <div style={{ maxWidth: 600, margin: '32px auto 0 auto' }}>
        {!showList ? (
          <EmployeeForm onSuccess={handleSuccess} />
        ) : (
          <EmployeeList />
        )}
      </div>
    </div>
  );
}

export default App;
