import React, { useState } from 'react';
import axios from 'axios';
import { Box, Button, TextField, Typography, MenuItem, Paper, Alert, CircularProgress } from '@mui/material';

function EmployeeForm({ onSuccess }) {
  const [form, setForm] = useState({
    fullName: '',
    nicNumber: '',
    dateOfBirth: '',
    sex: '',
    district: '',
    permanentAddress: '',
    temporaryAddress: '',
    contactDetails: '',
    photo: ''
  });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);


  const handleChange = e => {
    const { name, value, files } = e.target;
    if (name === 'photo' && files && files[0]) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setForm(prev => ({ ...prev, photo: reader.result.split(',')[1] }));
      };
      reader.readAsDataURL(files[0]);
    } else {
      setForm({ ...form, [name]: value });
    }
    setError('');
  };

  const validate = () => {
    if (!form.fullName || !form.nicNumber || !form.dateOfBirth || !form.sex || !form.district || !form.permanentAddress || !form.contactDetails) {
      setError('Please fill in all required fields.');
      return false;
    }
    return true;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!validate()) return;
    setLoading(true);
    try {
      await axios.post('http://localhost:5000/api/employees', form);
      setMessage('Employee added successfully!');
      setForm({
        fullName: '', nicNumber: '', dateOfBirth: '', sex: '', district: '', permanentAddress: '', temporaryAddress: '', contactDetails: '', photo: ''
      });
      setError('');
      if (onSuccess) onSuccess();
    } catch (err) {
      setMessage('');
      setError('Failed to add employee. Please check your data.');
    }
    setLoading(false);
  };

  return (
    <Paper elevation={3} sx={{ maxWidth: 500, mx: 'auto', my: 5, p: 4, borderRadius: 3 }}>
      <Typography variant="h5" align="center" gutterBottom>Add Employee</Typography>
      {message && <Alert severity="success" sx={{ mb: 2 }}>{message}</Alert>}
      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
      <Box component="form" onSubmit={handleSubmit} autoComplete="off" sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
        <TextField label="Full Name" name="fullName" value={form.fullName} onChange={handleChange} required />
        <TextField label="NIC Number" name="nicNumber" value={form.nicNumber} onChange={handleChange} required />
        <TextField label="Date of Birth" name="dateOfBirth" type="date" value={form.dateOfBirth} onChange={handleChange} required InputLabelProps={{ shrink: true }} />
        <TextField select label="Sex" name="sex" value={form.sex} onChange={handleChange} required>
          <MenuItem value="">Select Sex</MenuItem>
          <MenuItem value="Male">Male</MenuItem>
          <MenuItem value="Female">Female</MenuItem>
          <MenuItem value="Other">Other</MenuItem>
        </TextField>
        <TextField label="District" name="district" value={form.district} onChange={handleChange} required />
        <TextField label="Permanent Address" name="permanentAddress" value={form.permanentAddress} onChange={handleChange} required />
        <TextField label="Temporary Address" name="temporaryAddress" value={form.temporaryAddress} onChange={handleChange} />
        <TextField label="Contact Details" name="contactDetails" value={form.contactDetails} onChange={handleChange} required />
        <Button
          variant="outlined"
          component="label"
          sx={{ textAlign: 'left' }}
        >
          Upload Photo
          <input
            type="file"
            name="photo"
            accept="image/*"
            hidden
            onChange={handleChange}
          />
        </Button>
        {form.photo && (
          <Box mt={2} textAlign="center">
            <img
              src={`data:image/*;base64,${form.photo}`}
              alt="Preview"
              style={{ maxWidth: '100%', maxHeight: 180, borderRadius: 8, border: '1px solid #ccc' }}
            />
          </Box>
        )}
        <Button type="submit" variant="contained" color="primary" disabled={loading} sx={{ mt: 2 }}>
          {loading ? <CircularProgress size={24} color="inherit" /> : 'Add Employee'}
        </Button>
      </Box>
    </Paper>
  );
}

export default EmployeeForm;
