import React, { useState } from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Avatar, Box } from '@mui/material';
import axios from 'axios';

function EmployeeProfile({ open, onClose, employee, onUpdate }) {
    const [deleting, setDeleting] = useState(false);
    const handleDelete = async () => {
      if (!window.confirm('Are you sure you want to delete this employee?')) return;
      setDeleting(true);
      try {
        await axios.delete(`http://localhost:5000/api/employees/${employee._id}`);
        if (onUpdate) onUpdate();
        onClose();
      } catch (err) {
        alert('Failed to delete employee.');
      }
      setDeleting(false);
    };
  const [edit, setEdit] = useState(false);
  const [form, setForm] = useState(employee || {});
  const [saving, setSaving] = useState(false);

  React.useEffect(() => {
    setForm(employee || {});
    setEdit(false);
  }, [employee]);

  const handleChange = e => {
    const { name, value, files } = e.target;
    if (name === 'photo' && files && files[0]) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setForm(prev => ({ ...prev, photo: reader.result.split(',')[1] }));
      };
      reader.readAsDataURL(files[0]);
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      await axios.put(`http://localhost:5000/api/employees/${employee._id}`, form);
      setEdit(false);
      if (onUpdate) onUpdate();
    } catch (err) {
      alert('Failed to update employee.');
    }
    setSaving(false);
  };

  if (!employee) return null;

  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
      <DialogTitle sx={{ textAlign: 'center', fontWeight: 600, color: '#1976d2' }}>Employee Profile</DialogTitle>
      <DialogContent>
        <Box display="flex" flexDirection="column" alignItems="center" mb={2}>
          <Avatar src={form.photo ? `data:image/*;base64,${form.photo}` : undefined} sx={{ width: 160, height: 160, mb: 2, boxShadow: 3, border: '2px solid #1976d2' }} />
          {edit && (
            <Button variant="outlined" component="label" sx={{ mt: 1 }}>
              Upload New Photo
              <input
                type="file"
                name="photo"
                accept="image/*"
                hidden
                onChange={handleChange}
              />
            </Button>
          )}
        </Box>
        <TextField label="Full Name" name="fullName" value={form.fullName || ''} onChange={handleChange} fullWidth margin="normal" disabled={!edit} />
        <TextField label="NIC Number" name="nicNumber" value={form.nicNumber || ''} onChange={handleChange} fullWidth margin="normal" disabled={!edit} />
        <TextField label="Date of Birth" name="dateOfBirth" type="date" value={form.dateOfBirth ? form.dateOfBirth.substring(0,10) : ''} onChange={handleChange} fullWidth margin="normal" disabled={!edit} InputLabelProps={{ shrink: true }} />
        <TextField label="Age" name="age" value={form.age || ''} onChange={handleChange} fullWidth margin="normal" disabled={!edit} />
        <TextField label="Sex" name="sex" value={form.sex || ''} onChange={handleChange} fullWidth margin="normal" disabled={!edit} />
        <TextField label="District" name="district" value={form.district || ''} onChange={handleChange} fullWidth margin="normal" disabled={!edit} />
        <TextField label="Permanent Address" name="permanentAddress" value={form.permanentAddress || ''} onChange={handleChange} fullWidth margin="normal" disabled={!edit} />
        <TextField label="Temporary Address" name="temporaryAddress" value={form.temporaryAddress || ''} onChange={handleChange} fullWidth margin="normal" disabled={!edit} />
        <TextField label="Contact Details" name="contactDetails" value={form.contactDetails || ''} onChange={handleChange} fullWidth margin="normal" disabled={!edit} />
      </DialogContent>
      <DialogActions>
        <Box display="flex" justifyContent="space-between" width="100%">
          <Box>
            {!edit ? (
              <Button onClick={() => setEdit(true)} variant="contained">Edit</Button>
            ) : (
              <Button onClick={handleSave} variant="contained" disabled={saving}>{saving ? 'Saving...' : 'Save'}</Button>
            )}
            <Button onClick={onClose} color="secondary" sx={{ ml: 1 }}>Close</Button>
          </Box>
          <Button onClick={handleDelete} color="error" variant="outlined" disabled={deleting} sx={{ ml: 2 }}>
            {deleting ? 'Deleting...' : 'Delete Profile'}
          </Button>
        </Box>
      </DialogActions>
    </Dialog>
  );
}

export default EmployeeProfile;
