import React, { useState } from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Avatar, Box, Card, CardContent, Typography, Divider } from '@mui/material';
import axios from 'axios';

function EmployeeProfile({ open, onClose, employee, onUpdate }) {
    const [deleting, setDeleting] = useState(false);
    const handleDelete = async () => {
      if (!window.confirm('Are you sure you want to delete this employee?')) return;
      setDeleting(true);
      try {
        await axios.delete(`http://localhost:5000/api/employees/${employee._id}`);
        if (onUpdate) onUpdate();
        onClose();
      } catch (err) {
        alert('Failed to delete employee.');
      }
      setDeleting(false);
    };
  const [edit, setEdit] = useState(false);
  const [form, setForm] = useState(employee || {});
  const [saving, setSaving] = useState(false);

  React.useEffect(() => {
    setForm(employee || {});
    setEdit(false);
  }, [employee]);

  const handleChange = e => {
    const { name, value, files } = e.target;
    if (name === 'photo' && files && files[0]) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setForm(prev => ({ ...prev, photo: reader.result.split(',')[1] }));
      };
      reader.readAsDataURL(files[0]);
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      await axios.put(`http://localhost:5000/api/employees/${employee._id}`, form);
      setEdit(false);
      if (onUpdate) onUpdate();
    } catch (err) {
      alert('Failed to update employee.');
    }
    setSaving(false);
  };

  if (!employee) return null;

  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
      <Card sx={{ mt: 3, mb: 3, boxShadow: 6, borderRadius: 4, overflow: 'visible' }}>
        <Box sx={{ background: 'linear-gradient(90deg, #1976d2 60%, #42a5f5 100%)', p: 3, borderTopLeftRadius: 16, borderTopRightRadius: 16, textAlign: 'center' }}>
          <Avatar src={form.photo ? `data:image/*;base64,${form.photo}` : undefined} sx={{ width: 120, height: 120, mx: 'auto', mb: 1, boxShadow: 3, border: '3px solid #fff' }} />
          {edit && (
            <Button variant="contained" component="label" sx={{ mt: 1, background: '#fff', color: '#1976d2', fontWeight: 600 }}>
              Upload New Photo
              <input
                type="file"
                name="photo"
                accept="image/*"
                hidden
                onChange={handleChange}
              />
            </Button>
          )}
          <Typography variant="h5" sx={{ color: '#fff', fontWeight: 700, mt: 2, letterSpacing: 1 }}>Employee Profile</Typography>
        </Box>
        <CardContent>
          <Box display="flex" flexWrap="wrap" gap={2} justifyContent="center" mb={2}>
            <TextField label="Full Name" name="fullName" value={form.fullName || ''} onChange={handleChange} fullWidth margin="dense" disabled={!edit} sx={{ maxWidth: 260 }} />
            <TextField label="NIC Number" name="nicNumber" value={form.nicNumber || ''} onChange={handleChange} fullWidth margin="dense" disabled={!edit} sx={{ maxWidth: 260 }} />
            <TextField label="Date of Birth" name="dateOfBirth" type="date" value={form.dateOfBirth ? form.dateOfBirth.substring(0,10) : ''} onChange={handleChange} fullWidth margin="dense" disabled={!edit} InputLabelProps={{ shrink: true }} sx={{ maxWidth: 260 }} />
            <TextField label="Age" name="age" value={form.age || ''} onChange={handleChange} fullWidth margin="dense" disabled={!edit} sx={{ maxWidth: 120 }} />
            <TextField label="Sex" name="sex" value={form.sex || ''} onChange={handleChange} fullWidth margin="dense" disabled={!edit} sx={{ maxWidth: 120 }} />
            <TextField label="District" name="district" value={form.district || ''} onChange={handleChange} fullWidth margin="dense" disabled={!edit} sx={{ maxWidth: 180 }} />
            <TextField label="Permanent Address" name="permanentAddress" value={form.permanentAddress || ''} onChange={handleChange} fullWidth margin="dense" disabled={!edit} sx={{ maxWidth: 260 }} />
            <TextField label="Temporary Address" name="temporaryAddress" value={form.temporaryAddress || ''} onChange={handleChange} fullWidth margin="dense" disabled={!edit} sx={{ maxWidth: 260 }} />
            <TextField label="Contact Details" name="contactDetails" value={form.contactDetails || ''} onChange={handleChange} fullWidth margin="dense" disabled={!edit} sx={{ maxWidth: 260 }} />
          </Box>
        </CardContent>
        <Divider />
        <DialogActions sx={{ p: 2, display: 'flex', justifyContent: 'space-between' }}>
          <Box>
            {!edit ? (
              <Button onClick={() => setEdit(true)} variant="contained">Edit</Button>
            ) : (
              <Button onClick={handleSave} variant="contained" disabled={saving}>{saving ? 'Saving...' : 'Save'}</Button>
            )}
            <Button onClick={onClose} color="secondary" sx={{ ml: 1 }}>Close</Button>
          </Box>
          <Button onClick={handleDelete} color="error" variant="outlined" disabled={deleting} sx={{ ml: 2 }}>
            {deleting ? 'Deleting...' : 'Delete Profile'}
          </Button>
        </DialogActions>
      </Card>
    </Dialog>
  );
}

export default EmployeeProfile;
