import React, { useEffect, useState } from 'react';
import axios from 'axios';
import EmployeeProfile from './EmployeeProfile';
import { Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Avatar, Typography, TextField, Button, Box, Tooltip, InputAdornment } from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import SearchIcon from '@mui/icons-material/Search';


function EmployeeList({ onBack }) {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');
  const [profileOpen, setProfileOpen] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/employees')
      .then(res => {
        setEmployees(res.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to fetch employees');
        setLoading(false);
      });
  }, []);

  const showProfile = (emp) => {
    setSelectedEmployee(emp);
    setProfileOpen(true);
  };

  const handleProfileClose = () => {
    setProfileOpen(false);
    setSelectedEmployee(null);
  };

  const handleProfileUpdate = (updatedEmp) => {
    setEmployees(prev => prev.map(emp => emp._id === updatedEmp._id ? updatedEmp : emp));
    setProfileOpen(false);
    setSelectedEmployee(null);
  };

  // Filter employees by NIC number only
  const filtered = employees.filter(emp =>
    emp.nicNumber && emp.nicNumber.toLowerCase().includes(search.toLowerCase())
  );

  if (loading) return <Typography align="center" sx={{ mt: 4 }}>Loading employees...</Typography>;
  if (error) return <Typography color="error" align="center" sx={{ mt: 4 }}>{error}</Typography>;

  return (
    <Box
      sx={{
        minHeight: '100vh',
        width: '100vw',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #e3f2fd 60%, #fff 100%)',
        overflow: 'hidden',
      }}
    >
      <Paper
        elevation={6}
        sx={{
          width: '100%',
          maxWidth: 1400,
          p: { xs: 1, sm: 2, md: 4 },
          borderRadius: 4,
          boxShadow: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          mx: 'auto',
          overflow: 'visible',
        }}
      >
        <Typography
          variant="h4"
          align="center"
          sx={{
            color: '#1976d2',
            fontWeight: 700,
            mb: 3,
            letterSpacing: 1,
            fontSize: { xs: 24, sm: 32, md: 36 },
          }}
        >
          Employee List
        </Typography>
        <Box display="flex" justifyContent="center" alignItems="center" mb={3} width="100%">
          <TextField
            variant="outlined"
            placeholder="Search by NIC number"
            value={search}
            onChange={e => setSearch(e.target.value)}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon color="primary" sx={{ mr: 1 }} />
                </InputAdornment>
              )
            }}
            sx={{ width: { xs: '100%', sm: 340 }, borderRadius: 3, background: '#fff' }}
          />
        </Box>
        {filtered.length === 0 ? (
          <Typography align="center" color="text.secondary">No employees found.</Typography>
        ) : (
          <TableContainer
            sx={{
              borderRadius: 3,
              boxShadow: 3,
              width: '100%',
              maxWidth: 1350,
              mx: 'auto',
              background: '#fff',
            }}
          >
            <Table
              sx={{
                width: '100%',
                tableLayout: 'fixed',
                minWidth: 900,
              }}
              size="medium"
            >
              <TableHead sx={{ background: 'linear-gradient(90deg, #1976d2 60%, #42a5f5 100%)' }}>
                <TableRow>
                  <TableCell sx={{ color: '#fff', fontWeight: 700, fontSize: 16, py: 1, px: 1 }}>Photo</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700, fontSize: 16, py: 1, px: 1 }}>Full Name</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700, fontSize: 16, py: 1, px: 1 }}>NIC Number</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700, fontSize: 16, py: 1, px: 1 }}>Date of Birth</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700, fontSize: 16, py: 1, px: 1 }}>Age</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700, fontSize: 16, py: 1, px: 1 }}>Sex</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700, fontSize: 16, py: 1, px: 1 }}>District</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700, fontSize: 16, py: 1, px: 1 }}>Permanent Address</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700, fontSize: 16, py: 1, px: 1 }}>Temporary Address</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700, fontSize: 16, py: 1, px: 1 }}>Contact Details</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700, fontSize: 16, py: 1, px: 1 }}>Profile</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {filtered.map(emp => (
                  <TableRow key={emp._id} hover sx={{ background: '#f8fafc', fontSize: 15, height: 48 }}>
                    <TableCell sx={{ px: 1, py: 0.75 }}>
                      {emp.photo ? (
                        <Avatar src={`data:image/*;base64,${emp.photo}`} alt={emp.fullName} sx={{ width: 40, height: 40, mx: 'auto', border: '1px solid #1976d2' }} />
                      ) : (
                        <Avatar sx={{ width: 40, height: 40, mx: 'auto', bgcolor: '#bdbdbd' }}>
                          <PersonIcon sx={{ fontSize: 20 }} />
                        </Avatar>
                      )}
                    </TableCell>
                    <TableCell sx={{ fontSize: 15, px: 1, py: 0.75 }}>{emp.fullName}</TableCell>
                    <TableCell sx={{ fontSize: 15, px: 1, py: 0.75 }}>{emp.nicNumber}</TableCell>
                    <TableCell sx={{ fontSize: 15, px: 1, py: 0.75 }}>{new Date(emp.dateOfBirth).toLocaleDateString()}</TableCell>
                    <TableCell sx={{ fontSize: 15, px: 1, py: 0.75 }}>{emp.age}</TableCell>
                    <TableCell sx={{ fontSize: 15, px: 1, py: 0.75 }}>{emp.sex}</TableCell>
                    <TableCell sx={{ fontSize: 15, px: 1, py: 0.75 }}>{emp.district}</TableCell>
                    <TableCell sx={{ fontSize: 15, px: 1, py: 0.75 }}>{emp.permanentAddress}</TableCell>
                    <TableCell sx={{ fontSize: 15, px: 1, py: 0.75 }}>{emp.temporaryAddress}</TableCell>
                    <TableCell sx={{ fontSize: 15, px: 1, py: 0.75 }}>{emp.contactDetails}</TableCell>
                    <TableCell sx={{ px: 1, py: 0.75 }}>
                      {!profileOpen && (
                        <Tooltip title="Show Profile" arrow>
                          <Button
                            variant="contained"
                            color="primary"
                            size="medium"
                            sx={{ borderRadius: 2, fontWeight: 600, px: 1, letterSpacing: 1, fontSize: 13 }}
                            onClick={() => showProfile(emp)}
                          >
                            Profile
                          </Button>
                        </Tooltip>
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
        <EmployeeProfile
          open={profileOpen}
          onClose={handleProfileClose}
          employee={selectedEmployee}
          onUpdate={handleProfileUpdate}
        />
        {onBack && (
          <Button
            onClick={onBack}
            variant="contained"
            color="secondary"
            size="large"
            sx={{
              marginTop: 4,
              borderRadius: 3,
              px: 4,
              py: 1.5,
              fontWeight: 600,
              fontSize: 18,
              letterSpacing: 1,
              boxShadow: '0 2px 8px #b3c6e0',
              display: 'block',
              mx: 'auto',
            }}
          >
            ‚Üê Home
          </Button>
        )}
      </Paper>
    </Box>
  );
}

export default EmployeeList;
