import React, { useEffect, useState } from 'react';
import axios from 'axios';

function EmployeeList() {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/employees')
      .then(res => {
        setEmployees(res.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to fetch employees');
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading employees...</p>;
  if (error) return <p>{error}</p>;

  // Filter employees by NIC number only
  const filtered = employees.filter(emp =>
    emp.nicNumber.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h2>Employee List</h2>
      <input
        type="text"
        placeholder="Search by NIC number"
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ marginBottom: 16, padding: 8, width: 300, fontSize: 16 }}
      />
      {filtered.length === 0 ? (
        <p>No employees found.</p>
      ) : (
        <table border="1" cellPadding="8">
          <thead>
            <tr>
              <th>Full Name</th>
              <th>NIC Number</th>
              <th>Date of Birth</th>
              <th>Age</th>
              <th>Sex</th>
              <th>District</th>
              <th>Permanent Address</th>
              <th>Temporary Address</th>
              <th>Contact Details</th>
            </tr>
          </thead>
          <tbody>
            {filtered.map(emp => (
              <tr key={emp._id}>
                <td>{emp.fullName}</td>
                <td>{emp.nicNumber}</td>
                <td>{new Date(emp.dateOfBirth).toLocaleDateString()}</td>
                <td>{emp.age}</td>
                <td>{emp.sex}</td>
                <td>{emp.district}</td>
                <td>{emp.permanentAddress}</td>
                <td>{emp.temporaryAddress}</td>
                <td>{emp.contactDetails}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default EmployeeList;
