import React from 'react';
import EmployeeList from './EmployeeList';
import EmployeeForm from './EmployeeForm';
import { useState } from 'react';
import { Box, Typography } from '@mui/material';

function App() {
  const [showList, setShowList] = useState(false);
  const handleSuccess = () => setShowList(true);
  return (
    <Box minHeight="100vh" display="flex" flexDirection="column" alignItems="center" justifyContent="center" bgcolor="#f5f6fa">
      <Typography variant="h4" component="h1" sx={{ mb: 3, fontWeight: 600, color: '#1976d2', textAlign: 'center' }}>
        Employee System Frontend
      </Typography>
      {!showList ? (
        <>
          <EmployeeForm onSuccess={handleSuccess} />
          <Box mt={2} textAlign="center">
            <button onClick={() => setShowList(true)} style={{ padding: '8px 20px', fontSize: 16, borderRadius: 4, border: 'none', background: '#1976d2', color: '#fff', cursor: 'pointer' }}>Show Employee List</button>
          </Box>
        </>
      ) : (
        <EmployeeList />
      )}
    </Box>
  );
}

export default App;
